import React, { Fragment, useState } from 'react';


function Regis(){
    const [form, setForm] = useState({
        
        email : '',
        password : '',
        passord2 : ''

    }); 

    const [error, setError] = useState({
      
        email : '',
        password : '',
        passord2 : ''

    });

    const validate  = ()=>{
        const newError = { ...error }

        if(!form.email){
            newError.email = 'EMail Wajib Diisi';
        }
    }

    const handleChange = (e)=>{
        setForm({
            ...form,
            [e.target.name] : e.target.value
        }) 
        console.log(e.target.value);
    }

    const handleSubmit = (e)=>{
        e.preventDefault();
        alert('siap menyimpan');
    }

    return(
        <Fragment>
            <section className="container">
                <h1 className="large text-primary">sign up</h1>
                <p className="lead">create your acount here</p>
                <form className="form" onSubmit={ handleSubmit}>
                    <div className="form-group">

                    </div>
                    <div className="form-group">
                        <input type="email" onChange={handleChange} placeholder="Email Address" name="email" value={form.email}/>
                        <small className="form-text">This site uses Gravatar so if you want
                        a profile image, use a
                        Gravatar email</small>
                    </div>
                    <div className="form-group">
                        <input type="password" onChange={handleChange} placeholder="Password" name="password" value={form.password} minLength="6" />
                        </div>
                    <div className="form-group">
                        <input type="password" onChange={handleChange} placeholder="Confirm Password" name="password2" value={form.password2} minLength="6" />
                    </div>
                    <input type="submit" className="btn btn-primary" value="Register" />


                </form>
            </section>
        </Fragment>
        
    )
}

export default Regis;
