import React from 'react';
import PropTypes from 'prop-types';
import Moment from 'react-moment';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';



const PostItem = ({ auth, post : { _id, text, name, avatar, user, likes, comments, date}}) => {
    return (
        <div class="post bg-white p-1 my-1">
            <div>
                <a href="profile.html">
                <img class="round-img" src="https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50?s=200" alt="" />
                <h4>{name}</h4>
                </a>
            </div>
            <div>
                <p class="my-1">{text}</p>
                <p class="post-date">{date}</p>
                <button type="button" class="btn btn-light">
                    <i class="fas fa-thumbs-up"></i>
                    <span>{ likes.length > 0 && <span>{ likes.length}</span>}</span>
                </button>
                <button type="button" class="btn btn-light">
                    <i class="fas fa-thumbs-down"></i>
                </button>
    <Link to={`/post/${_id}`} className="btn btn-primary">Discussion {' '} { comments.length > 0 && <span class='comment-count'>{comments.length}</span>}</Link>
                <button type="button" class="btn btn-danger"><i className="fas fa-times"></i>
                </button>
            </div>
        </div>
       
    )
}

PostItem.propTypes = {
    auth : PropTypes.object.isRequired,
    post : PropTypes.object.isRequired
}

const mapStateToProps = (state)=>{
    return {
        auth : state.auth
    }
}

export default connect(mapStateToProps, {})(PostItem)
