import React, { Fragment, useState } from 'react';


function Regis(){
    const [form, setForm] = useState({
        name : '',
        email : '',
        password : '',
        passord2 : ''

    }); 

    const handleSubmit = (e)=>{
        e.preventDefault();
        alert('siap menyimpan');
    }

    return(
        <Fragment>
            <section className="container">
                <h1 className="large text-primary">sign up</h1>
                <p className="lead">create your acount here</p>
                <form className="form" onSubmit={ handleSubmit(e)}>
                    <div className="form-group">

                    </div>
                    <div className="form-group">
                        <input type="email" placeholder="Email Address" name="email" />
                        <small className="form-text">This site uses Gravatar so if you want
                        a profile image, use a
                        Gravatar email</small>
                    </div>
                    <div className="form-group">
                        <input type="password" placeholder="Password" name="password" minLength="6" />
                        </div>
                    <div className="form-group">
                        <input type="password" placeholder="Confirm Password" name="password2" minLength="6" />
                    </div>
                    <input type="submit" className="btn btn-primary" value="Register" />


                </form>
            </section>
        </Fragment>
        
    )
}

export default Regis;
