import { GET_POSTS, POST_ERROR } from '../actions/types';

const initialState = {
    posts : [],
    post : null,
    load: true,
    error : {}
}

export default function (state = initialState, action){
    const { type, payload } = action

    switch (type) {
        case GET_POSTS : 
            return {
                ...state,
                posts : payload,
                load :false
            }
        case POST_ERROR : 
            return { 
                ...state,
                load : true,
                error : payload
            }
        default :
            return state
    }
}
