import axios from 'axios';
import { setAlert } from './alert';
import { PROFILE_ERROR, GET_PROFILE} from './types';

export const getProfile = () => async dispatch=>{

    try{
        const res = await axios.get('/api/profile/me');

        dispatch({
            type : GET_PROFILE,
            payload : res.data
        })
    }catch(err){
        setAlert('')
    }

}
